mapper.select:
SELECT DISTINCT su.id AS id, su.tenant_id AS tenantId, su.account AS account, su.nickname AS nickname, su.real_name AS realName, su.gender AS gender, su.email AS email, su.mobile AS mobile, su.password AS password, su.status AS status, su.avatar AS avatar, su.country AS country, su.province AS province, su.city AS city, su.area AS area, su.street AS street, su.comments AS comments, su.create_time AS createTime, su.creator AS creator, su.update_time AS updateTime, su.operator AS operator, GROUP_CONCAT(DISTINCT sr.id) AS roleIds, GROUP_CONCAT(DISTINCT sr.role_key) AS roleKeys, GROUP_CONCAT(DISTINCT sr.role_name) AS roleNames, GROUP_CONCAT(DISTINCT sr.data_permission_type) AS dataPermissionTypes, GROUP_CONCAT(DISTINCT dataPermission.organization_id) AS organizationIds, so.id AS organizationId, so.organization_name AS organizationName FROM t_sys_user su INNER JOIN t_sys_user_role userRole ON su.id = userRole.user_id AND userRole.del_flag = 0 LEFT JOIN t_sys_role sr ON userRole.role_id = sr.id AND sr.del_flag = 0 LEFT JOIN t_sys_data_permission_user dataPermission ON su.id = dataPermission.user_id AND dataPermission.del_flag = 0 INNER JOIN t_sys_organization_user organizationUser ON su.id = organizationUser.user_id AND organizationUser.del_flag = 0 INNER JOIN t_sys_organization so ON organizationUser.organization_id = so.id AND so.del_flag = 0 WHERE su.del_flag = 0
AND su.id = #{user.id}
AND (su.account = #{user.account} OR su.nickname = #{user.account} OR su.mobile = #{user.account} OR su.email = #{user.account})
AND su.real_name = #{user.realName}
AND su.mobile = #{user.mobile}
AND su.email = #{user.email}
GROUP BY su.id

SELECT

FROM t_sys_user
WHERE del_flag = 0

    AND id != #{user.id}



        OR account = #{user.account} OR nickname = #{user.account} OR mobile = #{user.account} OR email = #{user.account}


        OR account = #{user.nickname} OR nickname = #{user.nickname} OR mobile = #{user.nickname} OR email = #{user.nickname}


        OR account = #{user.mobile} OR nickname = #{user.mobile} OR mobile = #{user.mobile} OR email = #{user.mobile}


        OR account = #{user.email} OR nickname = #{user.email} OR mobile = #{user.email} OR email = #{user.email}


ORDER BY id DESC

SELECT DISTINCT su.id AS id, su.tenant_id AS tenantId, su.account AS account, su.nickname AS nickname, su.real_name AS realName, su.gender AS gender, su.email AS email, su.mobile AS mobile, su.status AS status, su.avatar AS avatar, su.country AS country, su.province AS province, su.city AS city, su.area AS area, su.street AS street, su.comments AS comments, su.create_time AS createTime, su.creator AS creator, su.update_time AS updateTime, su.operator AS operator, GROUP_CONCAT(DISTINCT sr.id) AS roleIds, GROUP_CONCAT(DISTINCT sr.role_key) AS roleKeys, GROUP_CONCAT(DISTINCT sr.role_name) AS roleNames, so.id AS organizationId, so.organization_name AS organizationName FROM t_sys_user su INNER JOIN t_sys_user_role userRole ON su.id = userRole.user_id AND userRole.del_flag = 0 LEFT JOIN t_sys_role sr ON userRole.role_id = sr.id AND sr.del_flag = 0 INNER JOIN t_sys_organization_user organizationUser ON su.id = organizationUser.user_id AND organizationUser.del_flag = 0 INNER JOIN t_sys_organization so ON organizationUser.organization_id = so.id AND so.del_flag = 0 WHERE su.del_flag = 0
AND su.id = #{user.id}
and su.id in
#{userId}
AND su.account = #{user.account}
AND su.nickname = #{user.nickname}
AND su.real_name = #{user.realName}
AND su.mobile = #{user.mobile}
AND su.email = #{user.email}
AND su.status = #{user.status}
AND su.id IN (SELECT su.id FROM t_sys_user su INNER JOIN t_sys_user_role userRole ON su.id = userRole.user_id AND userRole.del_flag = 0 AND userRole.role_id = #{user.roleId})
AND sr.role_key = #{user.roleKey}
AND so.id = #{user.organizationId}
and so.id in
#{organizationId}
AND DATE_FORMAT(su.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{user.beginDateTime},'%Y-%m-%d')
AND DATE_FORMAT(su.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{user.endDateTime},'%Y-%m-%d')
GROUP BY su.id ORDER BY su.create_time DESC
